mapper.select:
SELECT COUNT(*) FROM tbl_team_bais a


		AND a.division_code IN

			#{item}



		AND a.region_code = #{filter.search_region_code}


		AND a.team_name LIKE CONCAT('%', #{filter.search_team_name}, '%')

SELECT a.team_id, a.team_name, a.team_logo_image, a.since_date, a.division_code, a.region_code, a.introduce, a.created_at, a.updated_at FROM tbl_team_bais a
AND a.division_code IN
#{item}
AND a.region_code = #{filter.search_region_code}
AND a.team_name LIKE CONCAT('%', #{filter.search_team_name}, '%')
ORDER BY a.team_id ASC

mapper.update:
UPDATE tbl_team_bais
team_name = #{team_name}, team_logo_image = #{team_logo_image}, since_date = #{since_date}, division_code = #{division_code}, region_code = #{region_code}, introduce = #{introduce}, updated_at = NOW()
WHERE team_id = #{team_id}
