mapper.select:
SELECT COUNT(*) FROM tbl_team_bais a


    AND a.team_id IN
    ( SELECT b.team_id
    FROM tbl_team_age_range b
    WHERE b.code_id IN 
    #{item}
  )


    AND a.team_id IN
    ( SELECT b.team_id
    FROM tbl_team_act_day b
    WHERE b.code_id IN 
    #{item}
  )


    AND a.team_id IN
    ( SELECT b.team_id
    FROM tbl_team_act_time b
    WHERE b.code_id IN 
    #{item}
  )


    AND a.region_code = #{filter.searchRegionCode}


    AND a.team_name LIKE CONCAT('%', #{filter.searchTeamName}, '%')

mapper.update:
UPDATE tbl_team_bais
team_name = #{teamName}, team_logo_image = #{teamLogoImage}, since_date = #{sinceDate}, home_court_id = #{homeCourtId}, region_code = #{regionCode}, introduce = #{introduce}, recruitment = #{recruitment}, is_recruiting = #{isRecruiting}, sns_address = #{snsAddress}, updated_at = NOW()
WHERE team_id = #{teamId}

mapper.insert:
INSERT
INTO tbl_team_member (
                       team_id, user_id, player_number, role, joined_at, introduction,
                       applicated_at
                     )
VALUES (
         #{teamId}, #{userId}, #{playerNumber}, #{role}, #{joinedAt}, #{introduction},
         #{applicatedAt}
       )

INSERT
INTO tbl_team_age_range (
                          team_id, code_id
                        )
VALUES (
         #{teamId}, #{codeId}
       )

INSERT
INTO tbl_team_act_time (
                         team_id, code_id
                       )
VALUES (
         #{teamId}, #{codeId}
       )

INSERT
INTO tbl_team_act_day (
                        team_id, code_id
                      )
VALUES (
         #{teamId}, #{codeId}
       )
